import json
import boto3

# AWS Functions
# S3 Functions
class s3:
    
    def __init__(self,bucket,filepath):
        self.bucket = bucket
        self.file = filepath
    
    def getfile(self):
        s3 = boto3.client("s3")
        bucket = self.bucket
        key = self.file
        try:
            fileObj = s3.get_object(Bucket=bucket, Key=key)
            fileData = json.loads(fileObj["Body"].read().decode('utf-8'))
        except Exception as err:
            return(err)
        else:
            return(fileData)